    private void btnBuscarCodigoFacturaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarCodigoFacturaActionPerformed

        try {
            if (btnHistorial.isSelected()) {
                historial = historial + "\n----NEW-----\n" + listaP.buscarPorCodigo(Integer.parseInt(fieldCodigoFactura.getText())).toString();
                areaResultadoBusqueda.setText(historial);
            } else {
                areaResultadoBusqueda.setText(listaP.buscarPorCodigo(Integer.parseInt(fieldCodigoFactura.getText())).toString());
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "no hay objeto");
        }


    }//GEN-LAST:event_btnBuscarCodigoFacturaActionPerformed

