    private void btnCancelarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCancelarActionPerformed
        listaP.borrarTodos();
        areaResultadoBusqueda.setText("Factura Cancelada");
    }//GEN-LAST:event_btnCancelarActionPerformed

    private void btnConfirmarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConfirmarActionPerformed
         fFac =new Fecha(
            Integer.parseInt(String.valueOf(cajaDia.getSelectedItem())),
            Integer.parseInt(String.valueOf(cajaMes.getSelectedItem())),
            2020);
        factura.setRealizada(fFac);
        factura.setVenta(listaP);
        System.out.println(factura.toString());
        Registro.getInstancia().setArchivo(listaP); //guarda la info en archivo

    }//GEN-LAST:event_btnConfirmarActionPerformed

    private void btnFacturaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFacturaActionPerformed
    //La factura de los objetos introducidos en el momento, no las del registro    
        
        factura.setVenta(listaP);
        areaResultadoBusqueda.setText(factura.toString());
        
    }//GEN-LAST:event_btnFacturaActionPerformed

         

